(()=>{"use strict";new class{constructor(t){this.img=null,this.selector="string"!=typeof t?t:document.querySelector(t),this.addBufferedImage()}getAttribute(t){return this.selector.getAttribute(t)}addBufferedImage(){fetch(this.getAttribute("data-src")).then((t=>t.arrayBuffer())).then((t=>{this.selector.src=window.URL.createObjectURL(new Blob([new Uint8Array(t)])),this.selector.setAttribute("rendered",!0)}))}}(".to-hydrate")})();